import HeaderMainPage from '@/components/HeaderMainPage.vue';
import PostModifyTemplate from '@/components/PostModifyTemplate.vue';
export default {
  name: 'create-post',
  components: {
    PostModifyTemplate,
    HeaderMainPage
  },

  data() {
    return {
      publication: {}
    };
  },

  created() {
    const postId = this.$route.params.id;
    console.log(postId);
    fetch(`http://localhost:3000/api/publications/` + postId, {
      method: "GET",
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': "Bearer " + sessionStorage.getItem("Token")
      }
    }).then(response => response.json()).then(data => {
      console.log(data);
      this.publication = data;
      this.firstname = this.publication.user.firstname;
      this.lastname = this.publication.user.lastname; // console.log(this.publications)
    });
  }

};
