import axios from "axios"; // import { json } from "body-parser";

export default {
  name: "Auth-View",

  data() {
    return {
      dataLogin: {
        username: null,
        password: null
      }
    };
  },

  methods: {
    mounted() {
      // GET request using axios with error handling
      axios("http://localhost:3000/api/user/auth", {
        method: "POST",
        body: JSON.stringify(this.dataLogin),
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      }).then(response => response.json());
    }

  }
};
