import UserSingleTemplate from "@/components/UserSingleTemplate.vue";
import HeaderMainPage from "@/components/HeaderMainPage.vue";
export default {
  components: {
    UserSingleTemplate,
    HeaderMainPage
  },

  data() {
    return {
      user: ""
    };
  },

  methods: {
    created() {
      const postId = this.$route.params.id;
      fetch("http://localhost:3000/api/users/" + postId).then(response => response.json()).then(data => {
        this.user = data;
        console.table(this.user);
      });
    }

  }
};
